</head>
<body class="data-control-id-31 bootstrap bd-body-7  bd-pagebackground  bd-margins">
    <header class="data-control-id-936270 bd-headerarea-1 bd-margins">
        <section class="data-control-id-2177950 bd-section-10 bd-page-width bd-tagstyles " id="section3" data-section-title="Full Width Menu">
    <div class="bd-container-inner bd-margins clearfix">
        <div class="data-control-id-2177958 bd-layoutbox-15 bd-page-width  bd-no-margins clearfix">
    <div class="bd-container-inner">
        @if (typeof pages !== 'undefined' && pages.length) {

    <nav class="data-control-id-2177968 bd-hmenu-3 bd-page-width " data-responsive-menu="true" data-responsive-levels="one level">

            <div class="data-control-id-2177967 bd-responsivemenu-2 collapse-button">
    <div class="bd-container-inner">
        <div class="bd-menuitem-7 data-control-id-2178170">
            <a  data-toggle="collapse"
                data-target=".bd-hmenu-3 .collapse-button + .navbar-collapse"
                href="#" onclick="return false;">
                    <span>Menu</span>
            </a>
        </div>
    </div>
</div>
            <div class="navbar-collapse collapse">

            <div class="data-control-id-2177966 bd-horizontalmenu-1 clearfix">
                <div class="bd-container-inner">

                    <ul class="data-control-id-2178213 bd-menu-1 nav nav-pills nav-center">
                        @{
                            var pageParents = page.parents ? page.parents : [];
                            var filterItems = function (items) {
                                return items.filter(function (i) {
                                    return i.showInHMenu;
                                });
                            };

                            var inParents = function (id) {
                                return pageParents.some(function (i) {
                                    return i.id === id;
                                });
                            };
                        }

                        @function subMenuBefore(level, hasMegamenu) {
                            <div class="bd-menu-2-popup @if (hasMegamenu && level === 3) { bd-megamenu-popup }">
                            @if (hasMegamenu && level === 2) {
                                <div class="bd-menu-2 bd-mega-grid data-control-id-2178190 bd-grid-2">
                                    <div class="container-fluid">
                                        <div class="separated-grid row">
                            } else {
                                <ul class="data-control-id-2178225 bd-menu-2">
                            }
                        }

                        @function subMenuAfter(level, hasMegamenu) {
                            @if (hasMegamenu && level === 2) {
                                        </div>
                                    </div>
                                </div>
                            } else {
                                </ul>
                            }
                            </div>
                        }

                        @function subMenuItemBefore(item, level, hasMegamenu) {
                            @{
                                var additionalClass = 'bd-sub-item';
                                if (hasMegamenu) {
                                    if (level === 2) {
                                        var responsive = {
                                            lg: item.columns_lg || '',
                                            md: item.columns_md || '',
                                            sm: item.columns_sm || '',
                                            xs: item.columns_xs || ''
                                        };

                                        var responsiveClasses = '',
                                            lastMode = '';

                                        for (var mode in responsive) {
                                            if ('xs' === mode) break;
                                            lastMode = mode;
                                            var value = responsive[mode];
                                            if (value) {
                                                responsiveClasses += ' col-' + mode + '-' + Math.round(12 / value);
                                            }
                                        }

                                        if (!responsiveClasses && lastMode) {
                                            for (var i = item.children.length; i <= 12; i++) {
                                                if (i && (12 % i === 0)) {
                                                    responsiveClasses += ' col-' + lastMode + '-' + Math.round(12 / i);
                                                    break;
                                                }
                                            }
                                        }

                                        additionalClass += ' bd-mega-item data-control-id-2178180 bd-menuitem-5';
                                        additionalClass += ' separated-item-8 ' + responsiveClasses;
                                    } else if (level === 3) {
                                        additionalClass += ' bd-mega-item data-control-id-2178203 bd-menuitem-6';
                                    }
                                }
                            }
                            @if (hasMegamenu && level === 2) {
                                <div class="data-control-id-2178226 bd-menuitem-2 @(additionalClass)">
                                    <div class="data-control-id-2178191 bd-griditem-8 bd-grid-item">
                            } else {
                                <li class="data-control-id-2178226 bd-menuitem-2 bd-sub-item @(additionalClass)">
                            }
                        }

                        @function subMenuItemAfter(level, hasMegamenu) {
                            @if (hasMegamenu && level === 2) {
                                    </div>
                                </div>
                            } else {
                                </li>
                            }
                        }

                        @function renderMenuItem(item) {
                            @{
                                var isActive = inParents(item.id) || page.id === item.id,
                                    itemHref = item.customUrl || (pathToRoot + item.filePath);
                            }
                            <a @if (isActive) { class="active" } href="@(itemHref)" @if (item.target) { target="@(item.target)" }>@(item.caption)</a>
                        }

                        @function renderHMenuLevels(item, level, hasMegamenu) {
                            @{ var items = filterItems(item.children); }
                            @if (items.length) {

                                @(subMenuBefore(level, hasMegamenu))

                                @for (var i = 0; i < items.length; i++) {
                                    @{ var subItem = items[i]; }

                                    @(subMenuItemBefore(item, level, hasMegamenu))

                                    @(renderMenuItem(subItem))

                                    @if (item.children) {
                                        @(renderHMenuLevels(subItem, level + 1, hasMegamenu))
                                    }

                                    @(subMenuItemAfter(level, hasMegamenu))
                                }

                                @(subMenuAfter(level, hasMegamenu))
                            }
                        }

                        @{ var items = filterItems(pages); }
                        @for (var i = 0; i < items.length; i++) {
                            @{
                                var levels = 'megamenu';
                                var item = items[i];

                                var hasMegamenu = levels === 'megamenu' && !!item.children && item.children.some(function (child) {
                                    return child.children && child.children.length && !item.megamenu;
                                });
                            }
                            <li class="data-control-id-2178214 bd-menuitem-1
                                        @if (item.children && filterItems(item.children).length) { bd-submenu-icon-only }
                                        bd-toplevel-item
                                        @if (hasMegamenu) { bd-has-megamenu bd-megamenu bd-megamenu-xs }
                                        "
                                    @if (hasMegamenu) {
                                        data-mega-width="@(item.megamenuWidth ? item.megamenuWidth : 'sheet')"
                                        data-mega-width-value="@(item.customMenuWidth ? item.customMenuWidth : '600')"
                                    }>

                                @(renderMenuItem(item))

                                @if (item.children) {
                                    @(renderHMenuLevels(item, 2, hasMegamenu))
                                }
                            </li>
                        }
                    </ul>

                </div>
            </div>


            </div>
    </nav>

}
    </div>
</div>
    </div>
</section>
</header>

